

/*MENU SLIDE*/

$('.toogle').on('click', function (e) {
  var className = $(this).attr('class');
  if (className == 'toogle toogle-close') {

      $(this).removeClass();
      $(this).addClass('toogle toogle-open');
      $('.contain-right').removeClass('toogle-false');
      $('.contain-right').addClass('toogle-true');
      $('.brand').addClass('brand-hover');
  } else {
      $(this).removeClass();
      $(this).addClass('toogle toogle-close');
      $('.contain-right').removeClass('toogle-true');
      $('.contain-right').addClass('toogle-false');
      $('.brand').removeClass('brand-hover');
  }

  console.log(className);
});