

/*
	
	**********
		Vote javasript handle
	***

*/


$(document).ready(function() {

	hoverColorVotes();
}); /*document ready-->*/



function hoverColorVotes()
{
	$(document).on('mouseover','.glyphicon-star', function(e) {
		$('.linear').css("z-index","0");
		$('.vote-icon span').removeClass('star-light');
		var item = $(this).attr('item');
		var total = $('.vote-icon span').length;
		if (1 < item < total) {
			$('span[class="glyphicon-star"]').removeClass('hover');
			for (var i = 1; i < item; i ++) {
				$('span[item="'+i+'"]').addClass('hover');
			}
			if (item < total) {
				for (var i = total; i > item; i --) {
					if ($('span[class="hover"]') != undefined) {
						$(this).removeClass('hover');
					}
				}
			} // IF
		} // IF
	});

	$(document).on('mouseout', '.glyphicon-star', function(e) {
		$('.linear').css("z-index", "700");
		$('.vote-icon span').addClass('star-light');
		$('.vote-icon span').removeClass('hover');
	});

	$(document).on('mouseover', '.linear', function (e) {
		$(this).css("z-index","0");
    });

	$(document).on('mouseout', '.linear', function (e) {
		$(this).css("z-index", "700");
    });
}